    x = e.width + this.chartLeft + 5;
  } else {
    this.ctx.fillStyle = "#fff";
    this.ctx.textAlign = "right";
  }

    this.ctx.fillText(e.number, x, e.y + this.barWidth / 2);
  });
  this.ctx.restore();

  // if this animation is over
  if (t === 1) {
    // stop this timer since we are done animating.
    this.timer.stop();
  }
},
